css:
* {
    font-size: 15px;
    box-sizing: border-box; /* Ensures padding and borders are included within the width and height */
}

.flex {
    display: flex;
    justify-content: space-between;
    border: 2px solid black;
    gap: 10px; /* Adds space between flex items to prevent border overlap */
}
.Heading {
    align-content: center;
    font-weight: bolder;
}

#Eighth_Section > div:last-child {
    border: 1px solid black; /* Reduce border thickness */
    padding: 5px; /* Adjust padding if needed */
}
.print-format img {
    max-width: 150px !important ;
}
